$(document).ready(function()
{
    pullNotification();
});

function pullNotification()
{
    getNotification();
    setTimeout(pullNotification,10000);
}


function getNotification()
{
    $.post(base_url + '/messages/get-notification', function(data)
    {
        if(data != 0){
            $(".badge").text(data);
        }else{
 			$(".badge").text("");
        }

    });
}