var username;
var authPic;

function movetoBottom()
{
var objDiv = document.getElementById("chat");
objDiv.scrollTop = objDiv.scrollHeight;
}

$(document).ready(function()
{
    username = $('.active').attr('id');
    authPic = $('#user-pic').attr('src');
    $("#badge-"+username).text("");


    pullAll();

    $(document).keyup(function(e) {

        if (e.keyCode == 13)
            sendMessage();
    });
});

function pullAll(){
    retrieveChatMessages();
    pullData()
}

function pullData()
{
    retriveNew();
    setTimeout(pullData,1500);
}

function setUsername(id){
    $('#chat-window').empty();
    username = id;
    $("#badge-"+username).text("");
    pullAll();
}

function retrieveChatMessages()
{
    $.post('messages/get', {userId: username}, function(data)
    {
        $.each(data, function(index, value) {
            var thumb= value['pic'].substr(0,value['pic'].length-4) + '-100x100' + value['pic'].substr(value['pic'].length-4, value['pic'].length);
            if(value['sender'] == 0){
                   $('#chat-window').append('<div class="your row"><img class="small-profile" src="http://localhost:8000/img/' + value['id'] +'/' + thumb +'"><div class="your-bubble"> '+ escapeHtml(value['message']) +'</div></div>');  

            }else{
                    $('#chat-window').append('<div class="their row"><img class="small-profile" src="http://localhost:8000/img/' + value['id'] +'/' + thumb +'"><div class="their-bubble">  '+ escapeHtml(value['message']) +'</div></div>');  
            }

            movetoBottom();
        });
    });
}

function retriveNew()
{
    $.post('messages/get-new', {userId: username}, function(data)
    {
        $.each(data, function(index, value) {
            var thumb= value['pic'].substr(0,value['pic'].length-4) + '-100x100' + value['pic'].substr(value['pic'].length-4, value['pic'].length);
            $('#chat-window').append('<div class="their row"><img class="small-profile" src="http://localhost:8000/img/' + value['id'] +'/' + thumb +'"><div class="their-bubble">  '+ escapeHtml(value['message']) +'</div></div>');  


            movetoBottom();
        });
    });
}

 var entityMap = {
    "&": "&amp;",
    "<": "&lt;",
    ">": "&gt;",
    '"': '&quot;',
    "'": '&#39;',
    "/": '&#x2F;'
  };

  function escapeHtml(string) {
    return String(string).replace(/[&<>"'\/]/g, function (s) {
      return entityMap[s];
    });
  }


function sendMessage()
{
    var text = $('#text').val();

    if (text.length > 0)
    {
        $.post('messages/send', {message: text, userId: username}, function()
        {
            $('#chat-window').append('<div class="your row"><div class="your-bubble">' + escapeHtml(text) +'</div></div>');
            $('#text').val('');
            movetoBottom()
        });
    }
}

$(document).ready(function(){
    movetoBottom();
});