var username;

$(document).ready(function()
{
    username = $('.active').attr('id');

    pullAll();

    $(document).keyup(function(e) {
        if (e.keyCode == 13)
            sendMessage();
    });
});

function pullAll(){
    retrieveChatMessages();
    pullData()
}

function pullData()
{
    retriveNew();
    setTimeout(pullData,1500);
}

function setUsername(id){
    $('#chat-window').empty();
    username = id;
    pullAll();
}

function retrieveChatMessages()
{
    $.post('messages/get', {userId: username}, function(data)
    {
        $.each(data, function(index, value) {
            if(index%2==0){
                $('#chat-window').append('<br><div>You: '+ escapeHtml(value) +'</div><br>');
            }else{
                $('#chat-window').append('<br><div>He: '+ escapeHtml(value) +'</div><br>');
            }
        });
    });
}

function retriveNew()
{
    $.post('messages/get-new', {userId: username}, function(data)
    {
        $.each(data, function(index, value) {
            if(index%2==0){
                $('#chat-window').append('<br><div>You: '+ escapeHtml(value) +'</div><br>');
            }else{
                $('#chat-window').append('<br><div>He: '+ escapeHtml(value) +'</div><br>');
            }
        });
    });
}

 var entityMap = {
    "&": "&amp;",
    "<": "&lt;",
    ">": "&gt;",
    '"': '&quot;',
    "'": '&#39;',
    "/": '&#x2F;'
  };

  function escapeHtml(string) {
    return String(string).replace(/[&<>"'\/]/g, function (s) {
      return entityMap[s];
    });
  }


function sendMessage()
{
    var text = $('#text').val();

    if (text.length > 0)
    {
        $.post('messages/send', {message: text, userId: username}, function()
        {
            $('#chat-window').append('<br><div>You: '+ escapeHtml(text) +'</div><br>');
            $('#text').val('');
        });
    }
}

